<?php declare(strict_types = 1);

namespace nplasencia\LeetcodeSolutions\Tests\LongestPalindromicSubstring;

use nplasencia\LeetcodeSolutions\LongestPalindromicSubstring\LongestPalindromicSubstring;
use PHPUnit\Framework\TestCase;

final class LongestPalindromicSubstringTest extends TestCase
{
	private LongestPalindromicSubstring $longestPalindromicSubstring;

	protected function setUp(): void
	{
		$this->longestPalindromicSubstring = new LongestPalindromicSubstring();
	}

	/**
     * @covers LongestPalindromicSubstring::longestPalindrome
	 * @dataProvider dataProvider
	 * @param string $input
	 * @param string $expectedPalindrome
	 */
	public function testLengthOfLongestSubstring(string $input, string $expectedPalindrome): void
	{
		$actual = $this->longestPalindromicSubstring->longestPalindrome($input);
		$this->assertEquals($expectedPalindrome, $actual);
	}

	public function dataProvider(): array
	{
		return [
            ['a', 'a'],
            ['aa', 'aa'],
            ['ccc', 'ccc'],
            ['aaaa', 'aaaa'],
            ['babad', 'bab'],
            ['cbbd', 'bb'],
            ['abcbaabcdcba', 'abcdcba'],
            ['aaaabaaa', 'aaabaaa'],
            ['', 'qgjjgq'],
            ['', 'uczcu'],
            ['', ''],
            ['', 'pppp'],
            ['', 'byyb']
		];
	}
}
